  def generateText(territory: TerritoryOccupationInfo): String = {
    val first = if (territory.noOfUnitsInBattle == 0) territory.noOfUnits else territory.noOfUnits - territory.noOfUnitsInBattle
    val second = if (territory.noOfUnitsInBattle == 0) territory.noOfProposedUnits else territory.noOfUnitsInBattle
    if (second <= 0)
      first.toString
    else
      first.toString + " + " + second.toString
  }

  def mapOfHungary(x: List[TerritoryOccupationInfo], colorMap: ColorMap, arrow: Option[(Int, Int)] = None, commander: Option[ClientEventHandler[TerritoryClicked]] = None): TagOf[SVG] = {
    <.svg(^.id:="map", ^.width:="1320px", ^.height:="840px", ^.viewBox:="0 0 800 506",
      (for (i <- 0 to 19) yield {
        val color: String = colorMap.colorOfRole(x(i).roleId).get.toString
        val numberColor = if (color == "white" || color == "yellow") "black" else "yellow"
        val text: String = generateText(x(i))
        val cb = Callback {
          commander.foreach(_.addAnEventToTheEventQueue(TerritoryClicked(i)))
        }
        <.g(japgolly.scalajs.react.vdom.html_<^.^.onClick --> cb,
          <.path(
            ^.fill:= color,
            ^.d:=myMap(i)._1
          ),
          <.title(myMap(i)._3),
          <.text(^.fill:=numberColor, ^.textAnchor:="middle", ^.x:=myMap(i)._2._1, ^.y:=myMap(i)._2._2, text)
        )
      }).toTagMod,
      Option.when(arrow.isDefined && arrow.get._1 >= 0 && arrow.get._1 < myMap.size && arrow.get._2 >= 0 && arrow.get._2 < myMap.size)
      ({
        val coordinate1 = myMap(arrow.get._1)._2
        val coordinate2 = myMap(arrow.get._2)._2
        <.g(
          <.circle(^.cx:=coordinate2._1, ^.cy:=coordinate2._2, ^.r:=3, ^.stroke:="black"),
          <.line(^.x1:=coordinate1._1, ^.x2:=coordinate2._1, ^.y1:=coordinate1._2, ^.y2:=coordinate2._2, ^.stroke:="black")
        )
      })
    )
  }

  def miniMap(territories: List[TerritoryOccupationInfo], colorMap: ColorMap, arrow: (Int, Int)): TagOf[SVG] = {
    // assert that they are neighbours
    import japgolly.scalajs.react.vdom.svg_<^.{<, ^}
    val from = MapGenerator.myMap(arrow._1)
    val to = MapGenerator.myMap(arrow._2)
    val rectangleOfFrom = HelperFunctions.calculateSmallestBoundingRectangleOfSvgPath(from._1)
    val rectangleOfTo = HelperFunctions.calculateSmallestBoundingRectangleOfSvgPath(to._1)

    val upperLeftCorner = (List(rectangleOfFrom._1, rectangleOfTo._1).min, List(rectangleOfFrom._2, rectangleOfTo._2).min)
    val lowerRightCorner = (List(rectangleOfFrom._3, rectangleOfTo._3).max, List(rectangleOfFrom._4, rectangleOfTo._4).max)
    val w = (lowerRightCorner._1 - upperLeftCorner._1).toInt
    val h = (lowerRightCorner._2 - upperLeftCorner._2).toInt

    val pushedFrom = HelperFunctions.pushSvgPath(from._1, upperLeftCorner)
    val pushedTo = HelperFunctions.pushSvgPath(to._1, upperLeftCorner)

    val fromTextCoordinatesOriginal = MapGenerator.myMap(arrow._1)._2
    val fromTextCoordinates = (fromTextCoordinatesOriginal._1 - upperLeftCorner._1.toInt, fromTextCoordinatesOriginal._2 - upperLeftCorner._2.toInt)
    val toTextCoordinatesOriginal = MapGenerator.myMap(arrow._2)._2
    val toTextCoordinates = (toTextCoordinatesOriginal._1 - upperLeftCorner._1.toInt, toTextCoordinatesOriginal._2 - upperLeftCorner._2.toInt)

    val fromText = generateText(territories(arrow._1))
    val toText = generateText(territories(arrow._2))

    <.svg(^.id:="minimap", ^.width:="500px", ^.height:="500px", ^.viewBox:="0 0 " + w + " " + h,
      <.g(
        <.path(
          ^.fill:= colorMap.colorOfRole(territories(arrow._1).roleId).get.toString,
          ^.d:=pushedFrom,
          <.title(from._3),
        ),
        <.text(^.fill:="black", ^.textAnchor:="middle", ^.x:=fromTextCoordinates._1, ^.y:=fromTextCoordinates._2, fromText)
      ),
      <.g(
        <.path(
          ^.fill:= colorMap.colorOfRole(territories(arrow._2).roleId).get.toString,
          ^.d:=pushedTo,
          <.title(to._3),
        ),
        <.text(^.fill:="black", ^.textAnchor:="middle", ^.x:=toTextCoordinates._1, ^.y:=toTextCoordinates._2, toText)
      ),
      <.g(
        <.circle(^.cx:=toTextCoordinates._1, ^.cy:=toTextCoordinates._2, ^.r:=3, ^.stroke:="black"),
        <.line(^.x1:=fromTextCoordinates._1, ^.x2:=toTextCoordinates._1, ^.y1:=fromTextCoordinates._2, ^.y2:=toTextCoordinates._2, ^.stroke:="black")
      )
    )
  }
